    // Event emitted when Ether is successfully withdrawn from the vault.
    // 'indexed' parameters allow for efficient filtering in logs.
    event WithdrawalMade(address indexed user, uint256 amount, uint256 newBalance);

    // Event emitted when an attempted withdrawal fails.
    // Important for understanding failed transactions.
    event WithdrawalFailed(address indexed user, uint256 amount, string reason);

    // --- Constructor ---
